package app.day3.example;

public class DoWhileLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 0;
		do {
			System.out.println(a);
			a++;
		} while (a < 10);
		
		for (int i =0; i < 3; i++) {
			System.out.println(i);
			System.out.println(i+1);
			System.out.println(i+2);
		}

	}

}
